import {
  Box,
  Button,
  ChakraProvider,
  extendTheme,
  FormControl,
  FormErrorMessage,
  FormHelperText,
  FormLabel,
  Grid,
  GridItem,
  Image,
  Input,
  Text,
} from "@chakra-ui/react";
import { useForm } from "react-hook-form";
import { z, ZodType } from "zod";
import { zodResolver } from "@hookform/resolvers/zod";
import { StepsStyleConfig, Step, Steps, useSteps } from "chakra-ui-steps";
import { FormField } from "./FormField";

type FormData = {
  email: string;
  password: string;
  confirmPasssword: string;
  firstName: string;
  lastName: string;
  age: number;
  location: string;
  gender: string;
  summery: string;
};

function Signup() {
  const schema: ZodType<FormData> = z
    .object({
      email: z.string().email().min(8).max(30),
      password: z.string().min(5).max(20),
      confirmPasssword: z.string().min(5).max(20),
      firstName: z.string().min(2).max(30),
      lastName: z.string().min(2).max(30),
      age: z.number().min(17).max(99),
      location: z.string().min(2).max(20),
      gender: z.string().min(2).max(20),
      summery: z.string().max(100),
    })
    .refine((data) => data.password === data.confirmPasssword, {
      message: "password do not match",
      path: ["confirmPassword"],
    });

  const {
    register,
    handleSubmit,
    formState: { errors },
    trigger,
  } = useForm<FormData>({
    resolver: zodResolver(schema),
  });
  const submitData = (data: FormData) => {
    console.log("it worked ", data);
  };

  const theme = extendTheme({
    components: {
      Steps: StepsStyleConfig,
    },
  });

  const { activeStep } = useSteps({
    initialStep: 0,
  });

  const {} = useForm();

  return (
    <ChakraProvider theme={theme}>
      <Grid
        templateAreas={`"header header"
                  "nav main"
                  `}
        gridTemplateRows={"170px 1fr 50px"}
        gridTemplateColumns={"900px 1fr"}
        h="950px"
        gap="1"
        color="blackAlpha.700"
        fontWeight="bold"
      >
        <GridItem pl="2" bg="orange.300" area={"header"}>
          <Image
            boxSize={"100px"}
            borderRadius={"50"}
            src="https://logowik.com/content/uploads/images/tinder4318.jpg"
            alt="tinder"
          />
          <Text fontSize={"5xl"} fontWeight={"bold"} textAlign={"center"}>
            {" "}
            LET'S SIGN UP!!
          </Text>
        </GridItem>

        <GridItem pl="3" bg="pink.300" area={"nav"}>
          <Text fontSize={"5xl"} fontWeight={"bold"} textAlign={"center"}>
            {" "}
            STEP 1
          </Text>
          <Box p={5}>
            <Steps activeStep={activeStep}>
              <Step label="Sign Up">
                <FormField label="Email">
                  <Input type="email" {...register("email")} />
                  {errors.email && (
                    <Text color="red">{errors.email.message}</Text>
                  )}
                </FormField>
                <FormField label="Password">
                  <Input type="password" {...register("password")} />
                  {errors.password && (
                    <Text color="red">{errors.password.message}</Text>
                  )}
                </FormField>
                <FormField label="Confirm Password">
                  <Input type="password" {...register("confirmPasssword")} />
                  {errors.confirmPasssword && (
                    <Text color="red">{errors.confirmPasssword.message}</Text>
                  )}
                </FormField>
              </Step>
              <Step label="About You">
                <FormField label="First Name">
                  <Input type="text" {...register("firstName")} />
                </FormField>
                <FormField label="Last Name">
                  <Input type="text" {...register("lastName")} />
                </FormField>
                <FormField label="Age">
                  <Input
                    type="number"
