import {
  Box,
  Button,
  ChakraProvider,
  extendTheme,
  Flex,
  FormControl,
  FormErrorMessage,
  FormHelperText,
  FormLabel,
  Grid,
  GridItem,
  Image,
  Input,
  Text,
} from "@chakra-ui/react";
import { useForm } from "react-hook-form";
import { z, ZodType } from "zod";
import { zodResolver } from "@hookform/resolvers/zod";
import { StepsStyleConfig, Step, Steps, useSteps } from "chakra-ui-steps";
import { FormField } from "./FormField";
import { type } from "os";

type FormAuthData = {
  email: string;
  password: string;
  confirmPassword: string;
};

type FormUserData = {
  firstName: string;
  lastName: string;
  age: number;
  location: string;
  gender: string;
  summery: string;
};

type FormLokingForData = {
  age: number;
  location: string;
  gender: string;
};

function Signup() {
  const AuthSchema: ZodType<FormAuthData> = z
    .object({
      email: z.string().email().min(8).max(30),
      password: z.string().min(5).max(20),
      confirmPassword: z.string().min(5).max(20),
    })
    .refine((data) => data.password === data.confirmPassword, {
      message: "passwords do not match",
      path: ["confirmPassword"],
    });

  const UserDataSchema: ZodType<FormUserData> = z.object({
    firstName: z.string().min(2).max(30),
    lastName: z.string().min(2).max(30),
    age: z.number().min(17).max(99),
    location: z.string().min(2).max(20),
    gender: z.string().min(2).max(20),
    summery: z.string().max(100),
  });

  const LookingForSchema: ZodType<FormLokingForData> = z.object({
    age: z.number().min(17).max(99),
    location: z.string().min(2).max(20),
    gender: z.string().min(2).max(20),
  });

  const {
    register,
    handleSubmit,
    formState: { errors },
    trigger,
  } = useForm<FormAuthData>({
    resolver: zodResolver(AuthSchema),
  });

  const submitData = (data: FormData) => {
    console.log("it worked ", data);
  };

  const theme = extendTheme({
    components: {
      Steps: StepsStyleConfig,
    },
  });

  const { activeStep, nextStep, prevStep } = useSteps({
    initialStep: 0,
  });

  const handleNextStep = async () => {
    let isValid = false;
    console.log(activeStep);

    switch (activeStep) {
      case 0:
        isValid = await trigger(["email", "password", "confirmPassword"]);
        break;
      case 1:
        isValid = await trigger([
          "firstName",
          "lastName",
          "age",
          "gender",
          "location",
          "summery",
        ]);
        break;
      case 2:
        isValid = await trigger(["gender", "age", "location"]);
    }
    console.log(isValid);

    if (isValid) nextStep();
  };

  const required = {
    value: true,
    message: "this field is requierd",
  };

  return (
    <ChakraProvider theme={theme}>
      <Grid
        templateAreas={`"header header"
                  "nav main"
                  `}
        gridTemplateRows={"170px 1fr 50px"}
        gridTemplateColumns={"900px 1fr"}
        h="950px"
        gap="1"
        color="blackAlpha.700"
        fontWeight="bold"
      >
        <GridItem pl="2" bg="orange.300" area={"header"}>
          <Image
            boxSize={"100px"}
            borderRadius={"50"}
            src="https://logowik.com/content/uploads/images/tinder4318.jpg"
            alt="tinder"
          />
          <Text fontSize={"5xl"} fontWeight={"bold"} textAlign={"center"}>
            {" "}
            LET'S SIGN UP!!
          </Text>
        </GridItem>

        <GridItem pl="3" bg="pink.300" area={"nav"}>
          <Text fontSize={"5xl"} fontWeight={"bold"} textAlign={"center"}>
            {" "}
            STEP 1
          </Text>
          <Box p={5}>
            <form onSubmit={handleSubmit(submitData)}>
              <Steps activeStep={activeStep}>
                <Step label="Sign Up">
                  <FormField label="Email" error={errors?.email?.message}>
                    <Input type="email" {...register("email", { required })} />
                    {errors.email && (
                      <Text color="red">{errors.email.message}</Text>
                    )}
                  </FormField>
                  <br />
                  <FormField label="Password" error={errors?.password?.message}>
                    <Input
                      type="password"
                      {...register("password", { required })}
                    />
                  </FormField>
                  <br />

                  <FormField
                    label="Confirm Password"
                    error={errors?.confirmPassword?.message}
                  >
                    <Input
                      type="password"
                      {...register("confirmPassword", { required })}
                    />
                  </FormField>
                </Step>
                <Step label="About You">
                  <FormField
                    label="First Name"
                    error={errors?.firstName?.message}
                  >
                    <Input
                      type="text"
                      {...register("firstName", { required })}
                    />
                  </FormField>
                  <FormField
                    label="Last Name"
                    error={errors?.lastName?.message}
                  >
                    <Input
                      type="text"
                      {...register("lastName", { required })}
                    />
                  </FormField>
                  <FormField label="Age" error={errors?.age?.message}>
                    <Input
                      type="number"
