import {
  FormControl,
  FormErrorMessage,
  FormHelperText,
  FormLabel,
  Grid,
  GridItem,
  Image,
  Input,
  Text,
} from "@chakra-ui/react";
import { useState } from "react";

function Signup() {
  const [input, setInput] = useState("");

  const handleInputChange = (e: any) => setInput(e.target.value);

  const isError = input === " ";
  return (
    <Grid
      templateAreas={`"header header"
                  "nav main"
                  `}
      gridTemplateRows={"170px 1fr 50px"}
      gridTemplateColumns={"750px 1fr"}
      h="950px"
      gap="1"
      color="blackAlpha.700"
      fontWeight="bold"
    >
      <GridItem pl="2" bg="orange.300" area={"header"}>
        <Image
          boxSize={"100px"}
          borderRadius={"50"}
          src="https://logowik.com/content/uploads/images/tinder4318.jpg"
          alt="tinder"
        />
        <Text fontSize={"3xl"} fontWeight={"bold"} textAlign={"center"}>
          {" "}
          LET'S SIGN UP!!
        </Text>
      </GridItem>
      <GridItem pl="1" bg="pink.300" area={"nav"}>
        <FormControl isInvalid={isError}>
          <FormLabel>Email:</FormLabel>
          <Input
            colorScheme={"blackAlpha"}
            type="email"
            value={input}
            onChange={handleInputChange}
          />
          {!isError ? (
            <FormHelperText>please Enter valid email.</FormHelperText>
          ) : (
            <FormErrorMessage>Email is required.</FormErrorMessage>
          )}
        </FormControl>{" "}
      </GridItem>
      <GridItem pl="2" bg="green.300" area={"main"}>
        Main
      </GridItem>
    </Grid>
  );
}
export default Signup;
