import {
  Box,
  Button,
  FormControl,
  FormErrorMessage,
  FormHelperText,
  FormLabel,
  Grid,
  GridItem,
  Image,
  Input,
  Link,
  Step,
  StepDescription,
  StepIcon,
  StepIndicator,
  StepNumber,
  Stepper,
  StepSeparator,
  StepStatus,
  StepTitle,
  Text,
  useSteps,
} from "@chakra-ui/react";
import { useForm } from "react-hook-form";
import { z, ZodType } from "zod";
import { zodResolver } from "@hookform/resolvers/zod";
import { Navigate, useNavigate } from "react-router-dom";

type FormData = {
  email: string;
  password: string;
  confirmPasssword: string;
  firstName: string;
  lastName: string;
  age: number;
  location: string;
  gender: string;
  summery: string;
};

const steps = [
  { title: "First", description: "Contact Info" },
  { title: "Second", description: "Date & Time" },
  { title: "Third", description: "Select Rooms" },
];

function Signup() {
  const navigate = useNavigate();

  const { activeStep, setActiveStep } = useSteps({
    index: 1,
    count: steps.length,
  });

  const schema: ZodType<FormData> = z
    .object({
      email: z.string().email().min(8).max(30),
      password: z.string().min(5).max(20),
      confirmPasssword: z.string().min(5).max(20),
      firstName: z.string().min(2).max(30),
      lastName: z.string().min(2).max(30),
      age: z.number().min(17).max(99),
      location: z.string().min(2).max(20),
      gender: z.string().min(2).max(20),
      summery: z.string().max(100),
    })
    .refine((data) => data.password === data.confirmPasssword, {
      message: "password do not match",
      path: ["confirmPassword"],
    });

  const { register, handleSubmit } = useForm<FormData>({
    resolver: zodResolver(schema),
  });
  const submitData = (data: FormData) => {
    console.log("it worked ", data);
  };
  return (
    <Grid
      templateAreas={`"header header"
                  "nav main"
                  `}
      gridTemplateRows={"170px 1fr 50px"}
      gridTemplateColumns={"650px 1fr"}
      h="950px"
      gap="1"
      color="blackAlpha.700"
      fontWeight="bold"
    >
      <GridItem pl="2" bg="orange.300" area={"header"}>
        <Image
          boxSize={"100px"}
          borderRadius={"50"}
          src="https://logowik.com/content/uploads/images/tinder4318.jpg"
          alt="tinder"
        />
        <Text fontSize={"5xl"} fontWeight={"bold"} textAlign={"center"}>
          {" "}
          LET'S SIGN UP!!
        </Text>
      </GridItem>

      <GridItem pl="3" bg="pink.300" area={"nav"}>
        <Text fontSize={"5xl"} fontWeight={"bold"} textAlign={"center"}>
          {" "}
          STEP 1
        </Text>

        <form onSubmit={handleSubmit(submitData)}>
          <label> Email:</label>
          <Input type="email" {...register("email")} />
          <br />
          <br />
          <label> Password:</label>
          <Input type="password" {...register("password")} />
          <br />
          <br />
          <label>Confirm Passsword:</label>
          <Input type="password" {...register("confirmPasssword")} />
          <br />
          <label>First Name:</label>
          <Input type="text" {...register("firstName")} />
          <br />
          <br />
          <label> Last Name:</label>
          <Input type="text" {...register("lastName")} />
          <br />
          <br />
          <label> Age:</label>
          <Input type="number" {...register("age", { valueAsNumber: true })} />
          <br />
          <br />
          <label> Location:</label>
          <Input type="text" {...register("location")} />
          <br />
          <br />
          <label> Gender:</label>
          <Input type="text" {...register("gender")} />
          <br />
          <br />
          <label> summery</label>
          <Input type="text" {...register("summery")} />
          <br />
          <br />
          <br />
          <br /> <br />
        </form>
        <Button
          dir="rigth"
          type="submit"
          loadingText="Submitting"
          colorScheme="teal"
          variant="outline"
          // onClick={navigate("/signup/step2")}
        >
          Submit
        </Button>
      </GridItem>
      <GridItem pl="2" bg="green.300" area={"main"}>
        <form onSubmit={handleSubmit(submitData)}>
          <Text fontSize={"5xl"} fontWeight={"bold"} textAlign={"center"}>
            {" "}
            About you:
          </Text>
        </form>
      </GridItem>
    </Grid>
  );
}
export default Signup;

