import {
  Box,
  Button,
  ChakraProvider,
  extendTheme,
  Flex,
  FormControl,
  FormErrorMessage,
  FormHelperText,
  FormLabel,
  Grid,
  GridItem,
  Image,
  Input,
  Text,
} from "@chakra-ui/react";
import { SubmitHandler, useForm } from "react-hook-form";
import { z, ZodType } from "zod";
import { zodResolver } from "@hookform/resolvers/zod";
import { StepsStyleConfig, Step, Steps, useSteps } from "chakra-ui-steps";
import { FormField } from "./FormField";
import { type } from "os";

type FormAuthData = {
  email: string;
  password: string;
  confirmPassword: string;
};

type FormUserData = {
  firstName: string;
  lastName: string;
  age: number;
  location: string;
  gender: string;
  summery: string;
};

type FormLokingForData = {
  age: number;
  location: string;
  gender: string;
};

type FormAllData = FormAuthData & FormUserData & FormLokingForData;

function Signup() {
  const AuthSchema: ZodType<FormAuthData> = z
    .object({
      email: z.string().email().min(8).max(30),
      password: z.string().min(5).max(20),
      confirmPassword: z.string().min(5).max(20),
    })
    .refine((data) => data.password === data.confirmPassword, {
      message: "passwords do not match",
      path: ["confirmPassword"],
    });

  const UserDataSchema: ZodType<FormUserData> = z.object({
    firstName: z.string().min(2).max(30),
    lastName: z.string().min(2).max(30),
    age: z.number().min(17).max(99),
    location: z.string().min(2).max(20),
    gender: z.string().min(2).max(20),
    summery: z.string().max(100),
  });

  const LookingForSchema: ZodType<FormLokingForData> = z.object({
    age: z.number().min(17).max(99),
    location: z.string().min(2).max(20),
    gender: z.string().min(2).max(20),
  });

  const {
    register,
    handleSubmit,
    formState: { errors },
    trigger,
  } = useForm<FormAuthData>({
    resolver: zodResolver(AuthSchema),
  });

  const {
    register: userRegister,
    handleSubmit: handleSubmitUser,
    formState: { errors: UserErrrors },
    trigger: triggerUser,
  } = useForm<FormUserData>({
    resolver: zodResolver(UserDataSchema),
  });

  const {
    register: LookingRegister,
    handleSubmit: handleSubmitLookingFor,
    formState: { errors: LookingForErrors },
    trigger: triggerLookingFor,
  } = useForm<FormLokingForData>({
    resolver: zodResolver(LookingForSchema),
  });

  // const submitData = (
  //   data: FormAllData,

  // ) => {
  //   console.log("it worked ", data);
  // };
  const handleSubmitAllData: SubmitHandler<FormAllData> = (
    data: FormAllData
  ) => {
    console.log("all data submitted", data);
  };

  const theme = extendTheme({
    components: {
      Steps: StepsStyleConfig,
    },
  });

  const { activeStep, nextStep, prevStep } = useSteps({
    initialStep: 0,
  });

  const handleNextStep = async () => {
    let isValid = false;
    console.log(activeStep);

    switch (activeStep) {
      case 0:
        isValid = await trigger(["email", "password", "confirmPassword"]);
        break;
      case 1:
        isValid = await triggerUser([
          "firstName",
          "lastName",
          "age",
          "gender",
          "location",
          "summery",
        ]);
        break;
      case 2:
        isValid = await triggerLookingFor(["gender", "age", "location"]);
    }
    console.log(isValid);

    if (isValid) nextStep();
  };

  const required = {
    value: true,
    message: "this field is requierd",
  };
  const { register: registertry, getValues } = useForm();

  return (
    <ChakraProvider theme={theme}>
      <Grid
        templateAreas={`"header header"
                  "nav main"
                  `}
        gridTemplateRows={"170px 1fr 50px"}
